precision mediump float;
#define PI 3.1415926535897932384626433832795

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    //Inputs for this fragment
    vec2 u_k = vec2(20,20); //"Zoom". You may derive this from the resolution if you want
    float u_time = iTime;
    vec2 v_coords = fragCoord / iResolution.xy;

    //Fragment value calculation done by applying the patterns described on the linked webpage
    float v = 0.0;
    vec2 c = v_coords * u_k - u_k/2.0;
    v += sin((c.x+u_time));
    v += sin((c.y+u_time)/2.0);
    v += sin((c.x+c.y+u_time)/2.0);
    c += u_k/2.0 * vec2(sin(u_time/3.0), cos(u_time/2.0));
    v += sin(sqrt(c.x*c.x+c.y*c.y+1.0)+u_time);
    v = v/2.0;

    //Color
    vec3 col = vec3(1, sin(PI*v), cos(PI*v)); //Color calculation. v is the "value" of the current fragment.
    fragColor = vec4(col*.5 + .5, 1); //Applies the fragment color, also makes the color brighter.
}
