//https://www.shadertoy.com/view/stGXDR

/*
uniform vec3      iResolution;           // viewport resolution (in pixels)
uniform float     iTime;                 // shader playback time (in seconds)
uniform float     iTimeDelta;            // render time (in seconds)
uniform int       iFrame;                // shader playback frame
uniform float     iChannelTime[4];       // channel playback time (in seconds)
uniform vec3      iChannelResolution[4]; // channel resolution (in pixels)
uniform vec4      iMouse;                // mouse pixel coords. xy: current (if MLB down), zw: click
uniform samplerXX iChannel0..3;          // input channel. XX = 2D/Cube
uniform vec4      iDate;                 // (year, month, day, time in seconds)
uniform float     iSampleRate;           // sound sample rate (i.e., 44100)
*/

#define PI 3.141592653

float f1(vec2 xy) { 
     return sin(xy.x+iTime);
}

float drawPoint(vec2 xy) {
    float line_thickness = 20./max(iResolution.x, iResolution.y);
    return 
    1.-smoothstep(
        0.,
        line_thickness,
        length(
            vec2(xy.x, f1(xy))-xy
         ));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    /*    _______ *after vec2 uv = fragCoord/iResolution.xy; 
        1|......|
    uv.y |......|
         |______|
      (0,0) uv.x 91        
    */

    vec2 uv = fragCoord/iResolution.xy;

    uv = uv*2.-1.; 
    uv *= PI;
    uv = vec2(
        length(uv),
        atan(uv.y, uv.x)/PI*.5+.5)*2.-1.;

    fragColor.rgb = vec3(drawPoint(uv));
    fragColor.a = 1.0; //Alpha/Opacity

/*
sin(OH)
cos(AH)
tan(OA) (Opposite Of is 'atan(uv.y,uv.x)')

so the aim is to display the ranges from a-z in one instance sitting ?
*/
}
