void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = texture(iChannel0, uv).xyz;
    col = pow(col, vec3(2.));
    col = sat(col*15.);
    col = mix(vec3(0.898,0.867,0.808), vec3(0.), col.x);
    fragColor = vec4(col,1.0);
}

