vec3 rdr(vec2 uv, vec2 ouv, float t)
{
    vec3 col = vec3(0.);

    uv.y += sin(uv.x*3.+t*.5)*.1;
    uv.x += sin((uv.y+uv.x)*2.-t*.35)*.3;
    col = pow(texture(iChannel0, uv*.5).x, 12.)*vec3(1.)*10.;

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 ouv = fragCoord/iResolution.xy;
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.xx;

    vec3 col = vec3(0.);
    for (int i = 0 ; i < 200; ++i)
        col += rdr(uv*2.5, ouv, float(i)*.01+iTime);
    col /= 200.;
    col = sat(col);
    float sz = .25;
    col = mix(col, vec3(0.), sat(max(abs(uv.x)-.47, abs(uv.y)-sz)*iResolution.x*.5));
   col = mix(col, texture(iChannel1, ouv).xyz, .975);
    fragColor = vec4(col,1.0);
}

