#define BASE 2.
#define lb(x) log(x)/log(BASE)
#define TAU acos(-1.)*2.


vec4 toTexture(vec2 uv)
{
    // This renders one 'tile'

    float a = iTime/10.0*TAU;
    uv -= cos(a*sqrt(2.));

    uv *= mat2(cos(a), sin(a), -sin(a), cos(a));

    uv *= sin(a)*0.5+0.6;

    uv += 0.5;

    return texture(iChannel0, uv);
}

vec4 sampleCoord(vec2 coord)
{
    float dirX = floor( lb(coord.x) );
    float dirY = floor( lb(coord.y) );

    float lenX = pow(BASE, dirX);
    float lenY = pow(BASE, dirY);

    float ux = (coord.x-lenX) / lenX / (BASE - 1.);
    float uy = (coord.y-lenY) / lenY / (BASE - 1.);

    if (dirX > dirY)
    {
        uy = (coord.y+lenX-lenX) / lenX / (BASE - 1.);
        dirY = dirX+1.;
    }

    else if (dirY > dirX)
    {
        ux = (coord.x+lenY-lenY) / lenY / (BASE - 1.);
        dirX = dirY+1.;
    }

    if (int(mod(abs(dirX), 2.)) == 0)
        ux = ux*-1.+1.;

    if (int(mod(abs(dirY), 2.)) == 0)
        uy = uy*-1.+1.;

    vec2 uv = vec2(ux, uy);

    return toTexture(uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = fragCoord/iResolution.yy;
    coord -= vec2(0.5*iResolution.x/iResolution.y, 0.5);

    float m = pow(BASE, mod(iTime*2., 2.));
    coord *= 1./m;

    float a = iTime/10.0*TAU;
    coord *= mat2(cos(a), sin(a), -sin(a), cos(a));
    vec4 col = sampleCoord(abs(coord));

    fragColor = col;
}
