// variant of https://shadertoy.com/view/ftyyWh

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = 3.* ( 2.*u - R ) / R.y;

    U.y = fract(U.y) 
        + .04* sign(mod(U.y,2.)-1.) * (fract(2.*U.x)-.5);

    O = step(.5,fract(U.xxxx)) * smoothstep( 9./R.y, 0., abs(U.y-.5)-.01 );
}
