// 133 chars - Golfed by Golfing Guru Xor and Jedi Master Fabrice!
void mainImage(out vec4 o,vec2 u)
{
    o.xyz = iResolution/25.;
    o = tanh((.5 - length( modf(u/o.y, u) -.5) ) * o.y)
        * texture(iChannel0, u * o.y/o.xy / 25.);
}


