// Standard linear interpolation
vec3 linpal( in float t, in vec3 a, in vec3 b, in vec3 c)
{
    return a*t + b*(1.0-t);
}

// Interpolation that bends the interpolation towards a third vector c
vec3 bendpal( in float t, in vec3 a, in vec3 b, in vec3 c )
{
    return a*t + b*(1.0-t) + c*sin(6.28*(t*0.5));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy / iResolution.xy;

    vec3                col = bendpal( p.x, vec3(1.0,0.0,0.0),vec3(0.0,0.0,1.0),vec3(0.3,0.0,0.3));
    if( p.y>(0.5))      col = linpal( p.x, vec3(1.0,0.0,0.0),vec3(0.0,0.0,1.0),vec3(1.0,1.0,1.0));

	fragColor = vec4( col, 1.0 );
}
