void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uvs = fragCoord/iResolution.xy;

    vec2 uvc =  uvs - vec2(0.5) +
        vec2(0.1*sin(iTime*0.3),0.1*cos(iTime*0.4));

    vec2 uv = fract(uvc*uvc*uvc * 14.0);


    float d = sqrt(uv.x*uv.x+uv.y*uv.y);

    float c = clamp(sin(d*50.0 + 12.0 *-iTime), 0.0, 1.0);

    float r = max(c*d, 0.0);

    // Output to screen
    fragColor = vec4(vec3(r, r*0.3*d, 0.4),1.0);
}
