vec4 bokeh(sampler2D tex, vec2 p, float radius, float ratio, float gamma, float samples)
{
    vec4 acc = vec4(0);
    float m = 1./sqrt(samples);
    for(float i = 0.; i < samples; i++)
    {
        vec2 th = vec2(2.399*i, sqrt(i)*m);
        th = vec2(cos(th.x)*ratio, sin(th.x))*th.y*radius;
        acc += pow(texture(tex, p+th), vec4(gamma));
    }
    return pow(acc/samples, 1./vec4(gamma));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    fragColor = vec4(bokeh(iChannel0, uv, max(0.0, length(uv-0.5)-DEADZONE)*BLUR, 1., 3., 16.).rgb, 1.0);
    //fragColor = texture(iChannel1, uv)+0.;
}

ivec2[] chars = ivec2[](
                ivec2(1, 11),
                ivec2(2, 11),
                ivec2(3, 11),
                ivec2(4, 11),
                ivec2(5, 11),
                ivec2(6, 11),
                ivec2(0, 12),
                ivec2(1, 12),
                ivec2(2, 12),
                ivec2(3, 12),
                ivec2(4, 12),
                ivec2(5, 12),
                ivec2(6, 12),
                ivec2(7, 12),
                ivec2(8, 12),
                ivec2(9, 12)
                );


vec4 getChar(ivec2 pos, vec2 uv)
{
    return texture(iChannel0, (vec2(pos)+uv)*0.0625);
}

vec4 getRandomChar(vec2 seed, vec2 uv)
{
    int index = int(hash21(seed)*float(chars.length()));
    return getChar(chars[index], uv);
}

float getCol(vec2 uv, float time, float px, float activated)
{
    float col = 1.0-smoothstep(0.0, px, getRandomChar(hash22(floor(uv)), fract(uv)).w - 0.5);
    col = floor(uv.y) <= activated? 0.0 : col-pow(min(1.0, (max(0., (floor(uv.y)-activated))-1.0)/TRAIL), 1./FALLOFF);

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x = (uv.x-0.5)*iResolution.x/iResolution.y+0.5;
    uv *= COUNT;
    float px = 1.777/iResolution.y*COUNT;
    float margin = 1.0 + TRAIL/COUNT;
    float activated = floor(COUNT-fract(
                            (COUNT-(floor(hash21(floor(uv.xx))*COUNT*2.0)/COUNT-iTime*SPEED))/margin
                            )*margin*COUNT);

    float col = 0.0;

    col = getCol(uv, iTime, px, activated);

    fragColor = vec4(vec3(clamp(col, 0., 1.)), 1);
}

#define COUNT 50.0
#define SPEED 0.3
#define TRAIL 20.0
#define FALLOFF 2.0
#define DISTORTION 0.5

#define BLUR 0.007
#define DEADZONE 0.2

float hash21(in vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))
                 * 43758.5453123);
}

vec2 hash22(vec2 p)
{
    vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

vec2 uvDistort(in vec2 uv, float str, float aspect)
{
    uv -= 0.5;
    uv.y *= aspect;

    uv *= str;
    uv += atan(uv)*length(uv)/str;
    uv.y /= aspect;
    uv += 0.5;

    if(hash21(floor(uv*100.0)) < 0.5)
        uv += (hash22(floor(uv*100.0)) - 0.5)*0.002;

    return uv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float px = 1.777/iResolution.y;
    uv = mix(uv, uvDistort(uv, 0.8, iResolution.y/iResolution.x), DISTORTION);
    fragColor = texture(iChannel0, uv)*vec4(0.172549019608, 0.878431372549, 0.0, 1.0);
    //fragColor = texture(iChannel1, uv);
    fragColor *= smoothstep(px, 0.0, length(clamp(uv, vec2(0.0), vec2(1.0)) - uv));
    fragColor *= smoothstep(-1., 1., sin(uv.y*500.0 + iTime * 20.)) * 0.3 + 0.7;
}
