#define PI 3.1415926

float Exponential_InOut (float amount) {
  if (amount == 0.) {
    return 0.;
  }
  if (amount == 1.) {
    return 1.;
  }
  if ((amount *= 2.) < 1.) {
    return 0.5 * pow(1024., amount - 1.);
  }
  return 0.5 * (-pow(2., -10. * (amount - 1.)) + 2.);
}

float Elastic_InOut (float amount) {
  if (amount == 0.) {
    return 0.;
  };
  if (amount == 1.) {
    return 1.;
  };
  amount *= 2.;
  if (amount < 1.) {
    return -0.5 * pow(2., 10. * (amount - 1.)) * sin((amount - 1.1) * 5. * PI);
  }
  return 0.5 * pow(2., -10. * (amount - 1.)) * sin((amount - 1.1) * 5. * PI) + 1.;
}

float plot(vec2 st, float pct){
  return smoothstep(fwidth(st.y - pct), 0.0, abs(st.y - pct));
}


float rect(vec2 st, vec2 size){
  size = 0.25-size*0.25;
  vec2 uv = smoothstep(size,size+size*vec2(0.001),st*(1.0-st));
  return uv.x*uv.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv = (uv - vec2(0., 0.15)) * vec2(1., 1.5);

    float time = fract(iTime*.8);
    float line = plot(uv, Elastic_InOut(uv.x));
    float rect = rect(uv+vec2(.5, .5)-vec2(time, Elastic_InOut(time)), vec2(.01, .01));

    vec3 col = vec3(line + rect);
    fragColor = vec4(col,1.0);
}
