#define pi 3.141592653589

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = gl_FragCoord.xy/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;
    st-=vec2(8./9.,0.5);
    float v = tan(st.x+0.5 ) * cos(st.y+0.5);
    float r =   abs(sin(iTime))/4. ;
    vec3 x = vec3(sin(  v *4000. )*r  +vec3(cos(iTime/3.),sin(iTime/3.),sin((pi+iTime)/3.))*0.02);
    float y = cos(v *4000.) *0.25  ;
    vec3 ci = vec3(smoothstep(distance(st, vec2(x.x ,y )) - 0.02 ,distance(st, vec2(x.x ,y )) + 0.02,0.1),
	 			smoothstep(distance(st, vec2(x.y ,y )) - 0.02 , distance(st, vec2(x.y ,y )) + 0.02 ,0.1),
     			smoothstep(distance(st, vec2(x.z ,y )) - 0.02 , distance(st, vec2(x.z ,y )) + 0.02 ,0.1 ));

    fragColor = vec4(ci*2.+vec3( float((step(1., mod((st.x)*8. + step(0.5,fract((st.y)*4.)),2.)) )/2. +-0.372 )) - (st.x) *(st.y)/1.5,1.0);
}
