//high frequency dither pattern appearing almost random without banding steps
//note: from "NEXT GENERATION POST PROCESSING IN CALL OF DUTY: ADVANCED WARFARE"
//      http://advances.realtimerendering.com/s2014/index.html
// Unreal version

float InterleavedGradientNoise(vec2 uv, int FrameId){
	// magic values are found by experimentation
	uv += float(FrameId)  * (vec2(47, 17) * 0.695f);

    vec3 magic = vec3( 0.06711056f, 0.00583715f, 52.9829189f );

    //https://juejin.cn/post/6844903687505068045
    //vec3 magic = vec3( 12.9898, 78.233, 43758.5453123 );

    return fract(magic.z * fract(dot(uv, magic.xy)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord * iResolution.xy;

    float rand = InterleavedGradientNoise(uv,iFrame % 8);

    // Output to screen
    fragColor = vec4(rand, rand, rand, 1);
}
