void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // having more than 8 levels is useless as the width of the bar becomes to little
    uv.y *= 8.0;

    float level = pow(3.0, floor(uv.y));

    uv.x *= level;
    uv.y = fract(uv.y);

    if (uv.y > 0.25 && uv.y < 0.75) {
        for (int i = 0; float(i) < level; i++) {
            if (mod(floor(uv.x), 3.0) == 1.0) {
                return;
            }
            uv.x /= 3.0;
        }

        fragColor = vec4(1.0);
    }
}
