// --------------------------------------------------------

#define S(y,f) smoothstep(f-0.05, f, y)-smoothstep(f, f+.05, y);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    uv*=8.;

    vec2 gv = fract(uv)-.5;


    float y1 = pow(uv.x, -1.);
    float y2 = pow(uv.x, 2.);        // f(x)=x^2
    float y3 = pow(uv.x, 3.);        // f(x)=x^3
    float y4 = sin(uv.x*sin(iTime*0.1)*10.);//noise
    float y5 = log(uv.x);            // f(x)=ln(x)


    float m = 0.;
    m += S(uv.y, y1);
    m += S(uv.y, y2);
    m += S(uv.y, y3);
    m += S(uv.y, y4);
    m += S(uv.y, y5);


    vec3 col;
    col.rg = floor(uv)*.15+0.1;
    if(gv.x>.48 || gv.y>.48) col=vec3(0.6,0.6,0.6);
    col += m * vec3(1.,1.,1.);

    // Output to screen
    fragColor = vec4(col,1.0);
}

