void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     float sinOffset = 0.5;
     float sinAmplitude = 0.25;
     float thickness = 0.02;
     float scrollSpeed = 0.5;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float sinwave = sinOffset + (sinAmplitude*sin((uv.y+(iTime*scrollSpeed))*tan(1.67)));

    if(uv.x >= sinwave + thickness || uv.x <= sinwave - thickness){ 
    fragColor = vec4(0,0,0,1);
    }
    else
    {
     fragColor = vec4(uv.x,uv.y,cos(iTime)*0.5, 1); 
    }

}
