void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy; //normalize UV cordinates to the length of the image 

    float f = uv.y ;
  //  f = log2(f);
   // f = abs(f);
  //  f = floor(f);
    f = pow(2.,f) * 2.;
    f = f * (uv.x + 1.);

    f += sin(iTime); //comment for a still image

    f = fract(f); // gives repeating patterns. it is the cousin on the modulus function
    f = round(f); // switch off to give gradient

	fragColor = vec4(f);
}
