// https://twitter.com/kamoshika_vrc/status/1407268416277282817

const float PI2 = acos(-1.)*2.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2. - iResolution.xy) / min(iResolution.x, iResolution.y);
    vec2 n = vec2(0);

    float N = ceil(mod(iTime, 10.));
    for(float i = 0.; i < 10.; i++){
        if(i >= N){
            break;
        }
        float a = i / N * PI2;
        vec2 q = (uv - vec2(cos(a), sin(a)) * 0.6) * 50.;
        float R = length(q);
        float T = iTime * 10.;
        float c = 0.05;
        n += q * (sin(R - T) * c - cos(R - T)) * exp(-R * c) / R;
    }

    vec3 lightDir = normalize(vec3(-1, 2, 5));
    vec3 normal = normalize(vec3(n, 1));

    float s = max(dot(lightDir, normal), 0.);
    vec3 col = vec3(pow(s, 60.) + s * 0.7);

    fragColor = vec4(col, 1.0);
}
