void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float writeColor = 0.01;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    vec4 color = texture(iChannel0, uv) / writeColor;

    vec4 tmpcol = vec4(0., pow(color.r, 1.0), 0., 1.);

    float current = texture(iChannel0, uv).r / writeColor;
    float right = texture(iChannel0, uv + vec2(1.0, 0.0) / iResolution.xy).r / writeColor;
    float bottom = texture(iChannel0, uv + vec2(0.0, 1.0) / iResolution.xy).r / writeColor;

    vec3 cp = cross(vec3(0.1, 0., right - current), vec3(0., 0.1, bottom - current));
    cp = normalize(cp);

    float dtp = dot(cp, vec3(1.0, -5.0, 0.)) * 0.4 + 0.1;

    tmpcol.g = pow(dtp, 0.6);

    fragColor = tmpcol;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float writeColor = 0.01;

    vec2 uv = fragCoord/iResolution.xy;

    if (iTime < 1.0 || iFrame == 0) {
        fragColor = vec4(length(texture(iChannel1, uv).xyz) / 3.0, 0., 0., 1.) * writeColor;
    }
    else {
        fragColor = texture(iChannel0, uv);
    }
}

vec4 fetchTx(vec2 uv, vec2 offset) {
    return texture(iChannel0, uv + offset / iResolution.xy);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float writeColor = 0.01;

    vec2 uv = fragCoord/iResolution.xy;

    float dt = iTimeDelta;

    dt = clamp(dt, 0., 0.05);

    float speedDcolor = 1.0;
    float speedColor = 10.0;


    float color = fetchTx(uv, vec2(0., 0.)).r / writeColor;

    float dcolor = fetchTx(uv, vec2(0., 0.)).g / writeColor;

    float l = fetchTx(uv, vec2(-1., 0.)).r / writeColor;
    float r = fetchTx(uv, vec2(1., 0.)).r / writeColor;
    float b = fetchTx(uv, vec2(0., 1.)).r / writeColor;
    float t = fetchTx(uv, vec2(0., -1.)).r / writeColor;

    dcolor += ((l + r + b + t) - color * 4.0) * dt * speedDcolor;

    color += dcolor * dt * speedColor;

    if (color >= 1.0) color = 1.0;
    if (color <= 0.0) color = 0.0;


    fragColor = vec4(color * writeColor, dcolor * writeColor, 0., 1.);
}
