const float RADIUS = 60.0;
const float TAU = 6.283;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 center = iResolution.xy / 2.0;
    vec2 relative = fragCoord - center;

    float dist = length(relative);
    float angle = atan(relative.y, relative.x) + TAU / 2.0;

    float threshold = iMouse.x / iResolution.x * 20.0;

    fragColor = vec4(angle - (threshold - floor(dist / RADIUS) * TAU));
}
