void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ 
    // from 1 to 201
    float count = floor((sin(iTime) + 1.) * 100. + 1.);

    vec2 tileSize = iResolution.xy / count;
    fragColor = texture(iChannel0, (floor(fragCoord / tileSize) * tileSize + tileSize / 2.) / iResolution.xy);
}
