void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    vec2 mxmy = iMouse.xy/iResolution.xy;

    vec2 dest = (uv - mxmy);

    float tOsc = sin(iTime * 0.5) * 0.5 + 0.5;

    float destl = pow(abs(dest.x), 2.) + pow(abs(dest.y), 2.);

    vec2 invDest = vec2(dest.x / destl, dest.y / destl) * 0.1;

    vec2 res = mxmy + invDest;

    //res = mxmy + dest;

    vec4 colorPre = texture(iChannel0, uv);
    vec4 colorPost = texture(iChannel0, res);


    // sample texture and output to screen
    //fragColor = colorPre;

    fragColor = colorPost;



}
