void mainImage(out vec4 c,in vec2 o){

  c=vec4(0);o-=iResolution.xy/2.0;//o*=640.0/iResolution.x;
  float t=iTime,dt=0.05;vec2 v1,v2,v3;
  for (float i=0.0;i<=16.0;i+=0.25){t=iTime/4.0-i*dt;
      v1=vec2(cos(t)*100.0,sin(t)*100.0);
      v2=vec2(sin(t*10.0)*33.0,cos((t*10.0))*33.0);
      v3=vec2(cos(t*15.0)*50.0,sin((t*15.0))*50.0);
      c+=vec4(length(o-v1-v2-v3)<length(o)/8.0)/(i*i+1.0)/2.0+vec4(length(o-v1-v2-v3)<length(o)/16.0)/(i*i+1.0);
  }
}
