void mainImage(out vec4 C, vec2 p){
    vec2 r = iResolution.xy;    ivec2 ir = ivec2(r);    ivec2 ip = ivec2(p); 

    //int
    if(abs(ip.y-(ir.y/2)) < ir.y/2 -20) 
        C = vec4(float((
            ip.y > ir.y/2
                ?ip.x+ip.y
                :ip.x < ir.x/2? ip.x: ip.y
            )%2>0)); 

    //float (incorrect to use float ops mod(fp.x,2.) due to .5 alignmnent)
    else { //p+=.5;
        C = vec4(mod(
            p.y > r.y/2.
                ?p.x+p.y
                :p.x < r.x/2.? p.x: p.y
            ,2.)); 
    }

}
