#define PI 3.14159265358979323846

//Screen
vec3 screen(vec3 base, vec3 bl_layer){
    return base + bl_layer - base * bl_layer;
}

vec2 rotate2D(vec2 _st, float _angle){
    _st -= 0.5;
    _st =  mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle)) * _st;
    _st += 0.5;
    return _st;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    vec4 tex0 = texture(iChannel0, uv);
    uv = rotate2D(uv, 0.);// set PI*0.5 instead of 0. if screen is portrait oriented
    uv.x += sin((iTime/5.))*0.05;
    vec4 tex1 = texture(iChannel1, (uv/2.+0.25)+sin(iTime/4.)*0.0125);
    vec4 tex1a = texture(iChannel1, (uv/(2.+sin(iTime)*0.01)+0.25)+cos(iTime/4.)*0.0125);

    vec4 tex1b = texture(iChannel1, uv/2.+0.4+cos(iTime/4.)*0.0125);
    vec4 tex1c = texture(iChannel1, (uv/(2.-cos(iTime/4.)*0.2)+0.4)+sin(iTime/4.)*0.0125);


    // Output to screen
    float alpha = 0.95;
    fragColor = mix(tex0, vec4(screen(tex0.rgb, tex1.rgb), 1.0), alpha);
    fragColor = mix(fragColor, vec4(screen(fragColor.rgb, tex1a.rgb), 1.0), alpha);

    fragColor = mix(fragColor, vec4(screen(fragColor.rgb, tex1b.rgb), 1.0), alpha);
    fragColor = mix(fragColor, vec4(screen(fragColor.rgb, tex1c.rgb), 1.0), alpha);
}
