float z = 4./360.;                     // characters size

void mainImage( out vec4 O, vec2 U ) {
    if (iMouse.z>0.) z /= .5 + 4.*iMouse.y/iResolution.y; // mouse zoom
    U *= z;
    U += mod(iTime/vec2(1,16.),16.) ;

    vec2 G = texture(iChannel0,  U / 16.).gb - .5,
         F = fract(U)  *2. - 1.,
         I = floor(U); 
         G.y = -G.y;

    G = mix(.3*F, G, smoothstep(1.,.5, max(abs(F.x),abs(F.y))));
 // G = mix(.4*F, G, smoothstep(1.,.7, length(F)));
    G *=  mod(I,2.)*2.  - 1.; // checker-reverse char depth for slope continuity

    O = vec4( .5+dot( G , cos(iTime+vec2(0,11)) ) ); 
 // O = vec4(.5+G,0,0);       // test: show colors
}
