float h11(float t){
    return fract(sin(52.3654*t+23.58)*3522.58);
}
float h21(vec2 st){
    return fract(sin(dot(st,vec2(23.658,45.633))+12.36)*558.366);
}
float noise(float t){
    float i=floor(t),f=fract(t);
    return mix(h11(i),h11(i+1.),smoothstep(0.,1.,f));
}

vec2 point(vec2 i){
    float t = iTime/2.;
    t=t*smoothstep(0.,1.,h21(i)+noise(t*.05+i.x+i.y)+.1)*.7;
    return.5+.8*(smoothstep(.1,.9,h21(i*8.+35.36))-.5)*vec2(cos(t)*h11(dot(i,vec2(21.54,265.4))+15.566),sin(t)*2.*(h11(dot(i,vec2(12.23,45.1))+2.28)-.5));
}

float line(vec2 st,vec2 p){
    float l=length(p);p=normalize(p);
    float res;
    res=1.-length(st-p*dot(p,st));

    vec2 proj=p*dot(p,st);
    res*=step(0.,dot(proj,p))*step(dot(proj,p),l);
    return res;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st=gl_FragCoord.xy/iResolution.xy;
    st=10.*(st-.5);
    st.x*=iResolution.x/iResolution.y;

    vec2 i=floor(st),f=fract(st);

    vec3 b=vec3(1.);
    b*=smoothstep(.1,.11,length(f-point(i)));
    for(int k=-1;k<=1;k++){
        for(int l=-1;l<=1;l++){
            if((k==0&&l!=0)||(k!=0&&l==0)){
                vec2 ni=vec2(float(k),float(l))+i;
                b*=smoothstep(.015,.03,1.-line(f-point(i),point(ni)+ni-i-point(i)));
            }
        }
    }

    vec3 color=1.-b;

    fragColor=vec4(color,1.);
}
