vec4 char(vec2 p, int c) {

    // if (p.x<.0|| p.x>1. || p.y<0.|| p.y>1.) return vec4(0,0,0,1.0);

	return texture(iChannel1, p/16. + fract(vec2(c, 15-c/16) / 16.));
}


const float pixel_size = 0.01;
const float letter_change_speed = 0.01;
const float wall_speed = 0.6;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    float aspect_ratio = iResolution.x / iResolution.y;

    vec2 noise_uv = uv - mod(uv, vec2(pixel_size/aspect_ratio, pixel_size));
    // vec3 pixel = texture(iChannel0, noise_uv).rgb;
    noise_uv.y = mod(noise_uv.y + letter_change_speed * iTime, 1.0);

    float _noise = texture(iChannel2, noise_uv).r;
    float row_noise = texture(iChannel2, vec2(noise_uv.x, 0.58)).r;
    float noise = round(_noise * 25.0 + 65.0);


    vec2 pixel_uv = (
        vec2((1.0/pixel_size)*aspect_ratio, (1.0/pixel_size)) *  
        mod(uv, vec2(pixel_size/aspect_ratio, pixel_size))
    );


    vec3 char = char(pixel_uv, int(noise)).rgb;
    // float intensity = length(pixel) / sqrt(3.0);

    /////////////////////////////////
    /////////////////////////////////



    noise_uv.y = mod(2.0 * row_noise + uv.y * 4.0 + wall_speed * iTime, 1.0);
    float green = char.r * (1.0 - noise_uv.y); // * 2.0 * intensity;



    /////////////////////////////////
    /////////////////////////////////

    vec3 col = vec3(0.0, green, 0.0);



    fragColor = vec4(col,1.0);
}
