float sdf(vec3 p) {

	return length(mod(p, vec3(1.0,1,0))-vec3(1,1,0))+.2;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec3 position = vec3(.0, .0, 0.0);
	vec3 direction = normalize(vec3(gl_FragCoord.xy/vec2(1),30.6));

	float g=3.;

	float eval = g;
	float dist = g;
	int k = 0;

	while(eval > .01*100. && k<3) {

	position -= direction ;
	dist *= 5.;
	eval = sdf(position);
	++k;
	 }

	fragColor = vec4(0,0,exp(-dist*0.01),  1.0);
}
