float circle (vec2 st, float pct){
    if(st.y<2.5)return 0.;
  return  step(distance(st,vec2(2.5)+vec2(0,sin(iTime)*2.5)), pct);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 st = fragCoord/iResolution.y*5.0-vec2(2.,0.0);
    vec3 color = vec3(0.265,0.48,0.65);

    float pct = 0.2;
    float intensity =distance(st,vec2(2.5)+vec2(0,sin(iTime)*2.5));
    color = color+vec3(1.000,0.800,0.306)*pow(4.132,-2.0*intensity);
    color = mix(color,vec3(0.979,0.978,1.000),circle(st,pct));
    color =mix(color,mix(vec3(0.1,0.2,0.3),vec3(0.2,0.4,0.8),st.y),1.0-step(2.5,st.y));
    fragColor = vec4(color,1.0);
}
