void mainImage(out vec4 fragColor, vec2 fragCoord)
{
    // X coordinate of current pixel as a fraction of resolution
    float x = fragCoord.x / iResolution.x;

    // Shift X to be in the range of -1..1
    x = x * 2.0 - 1.0;

    // Angle for vector rotation at current pixel
    float angle = radians(90.0) * x;

    // Rotation matrix for current angle
    float s = sin(angle);
    float c = cos(angle);

    mat3 rotationY = mat3(
        c, 0, -s,
        0, 1, 0,
        s, 0, c);

    // Vector to rotate
    vec3 vec = vec3(0, 0, 1);

    // Rotation
    vec *= rotationY;

    // Render result
    fragColor = vec4(vec, 1.0);
}
