bool test(vec2 uv){
    float k = 0.5;
    float wd = 0.03;

    if ( uv.y > k && uv.x-(uv.y-k) > k && uv.x-(uv.y-k) < wd+k )
    {
        return true;
    }
    if ( uv.y > k && uv.x+(uv.y-k) > k && uv.x+(uv.y-k) < wd+k )
    {
        return true;
    }
    if ( uv.y < k && k < uv.x && uv.x < wd+k )
    {
        return true;
    }
    return false;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv*abs(sin(iTime*0.015));
    // Time varying pixel olor
    vec3 col = 0.5 + 0.5*cos(uv.xyx+vec3(0,2,4));

    for( float i =0.1; i < 5. ; ++i){
        float a = 0.0003*i;
        if (test(((fract((uv+vec2(a,a))*(9.3*i)))))){
            col = vec3(1.0,0.0,0.0);
        }if (test(((fract((uv-vec2(a,-a))*(9.3*i)))))){
            col = vec3(0.0,1.0,0.0);
        }if (test(((fract((uv-vec2(-a,a))*(9.3*i)))))){
            col = vec3(0.0,0.0,1.0);
        }

        if (test(((fract(uv*(10.3*i)))))){
            col = vec3(1.0);
        }
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
