Main 
    Q = vec4(0);
    float a = .1;
    vec4 d = D(U);
    vec4 b = B(d.xy);
    if (length(b.xy-b.zw)<10.)
    Q = (.5+.5*sin(1.+.5*sin(.16*d.x)+vec4(1,2,3,4)))*exp(-sg(U,b.xy,b.zw));
