vec2 R; float T; int I;
#define Main void mainImage(out vec4 Q, in vec2 U) { R = iResolution.xy; T = iTime; I = iFrame;
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define C(U) texture(iChannel2,(U)/R)
#define D(U) texture(iChannel3,(U)/R)
#define N 7
float sg (vec2 p, vec2 a, vec2 b) {
    float i = clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.);
	float l = (length(p-a-(b-a)*i));
    return l;
}

Main
    float x = floor(U.x)+.5;
    float y = U.y;
    Q = A(vec2(x,y));

    vec2 f = vec2(3e-5,-1e-4);

    for(float i = -20.; i <=20.; i++)
    {
        vec4 q = A(mod(vec2(x,y+i),R));
        vec2 r = q.xy-Q.xy;
        float l = length(r);
        if (l>0.&&l<10.){
            f += r/l/40.*(l-abs(i));
        }
    }
    if (length(Q.zw)>1.) Q.zw = normalize(Q.zw);
    Q.zw += f;
    Q.xy += f+Q.zw;

    if (R.y-U.y<2.||I<1) {
        U.x = x+10.*sin(2e-3*float(I)+x)+10.*sin(5e-3*float(I)+.1*x);
        Q = vec4(U,0,0);
    }
}

void X (inout vec4 Q, vec2 U, vec4 a) {
    vec4 q = B(Q.xy);
    vec4 b = B(a.xy);
    if(sg(U,b.xy,b.zw)<sg(U,q.xy,q.zw))
        Q = a;
}
Main
    Q = C(U);
    if (I%N==0) Q=vec4(U,0,0);
    else {
        float k = exp2(float(N-1-(iFrame%N)));
    	X(Q,U,C(U+vec2(0,k)));
    	X(Q,U,C(U+vec2(k,0)));
    	X(Q,U,C(U-vec2(0,k)));
    	X(Q,U,C(U-vec2(k,0)));
    }
}

void X (inout vec4 Q, vec2 U, vec4 a) {
    vec4 q = B(Q.xy);
    vec4 b = B(a.xy);
    if(sg(U,b.xy,b.zw)<sg(U,q.xy,q.zw))
        Q = a;
}
Main
    Q = D(U);
    X(Q,U,D(U+vec2(0,1)));
    X(Q,U,D(U+vec2(1,0)));
    X(Q,U,D(U-vec2(0,1)));
    X(Q,U,D(U-vec2(1,0)));
    X(Q,U,vec4(Q.x,Q.y+1.,0,0));
    X(Q,U,vec4(Q.x,Q.y-1.,0,0));
    X(Q,U,C(U));
}
