#define scale 0.02
#define timeScale 1.

float rand (vec2 st) {
    return fract(sin(dot(st.xy,vec2(12.9898,78.233)))*43758.5453123);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float T = mod(iTime, 7000.);

    vec2 uv = fragCoord;
    uv *= scale;

    uv -= iMouse.xy*.02;
    uv.y += T;
    vec2 ipos = floor(uv);
    vec2 fpos = fract(uv);

    vec3 fTime;
    fTime.x = floor(T*1.*timeScale)*131.;
    fTime.y = floor(T*2.*timeScale)*273.;
    fTime.z = floor(T*4.*timeScale)*398.;

    vec3 col;
    col.r = round(rand(ipos+1.+fTime.z)+0.27);
    col.g = round(rand(ipos+10.+fTime.z)+0.27);
    col.b = round(rand(ipos+100.+fTime.z)+0.27);

    float tiles = smoothstep(.999, 1.,

        abs(fpos.x-round(rand(ipos+2.+fTime.x))) 
        + abs(fpos.y-round(rand(ipos+20.+fTime.x)))
        + round(rand(ipos+1.+fTime.x)+0.15)
    );

    col *= tiles;

    fragColor = vec4(col,1.0);
}
