void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float angle = 0.2 *iTime * 0.3;
    vec2 normalizedCoords = fragCoord / iResolution.xy*2.0-1.0;

    for(float i = 0.0; i < 32.0; i += 1.0) {
        normalizedCoords = abs(normalizedCoords);
        normalizedCoords -= 0.5;
        normalizedCoords *= 1.1;
        normalizedCoords *= mat2(
            cos(angle), -sin(angle),
            sin(angle), cos(angle)
        );
    }
    fragColor = vec4(vec3(length(normalizedCoords)),1.0);
}
