void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy; //Получаем координаты текущего пиксе
    xy.x = xy.x / iResolution.x; //Делим координаты на размер экрана
    xy.y = xy.y / iResolution.y; // Теперь x для самого левого пикселя равен 0, а для самого правого равен 1

    vec4 LaColora = vec4(0,0.0,0.0,1.0); //Теперь он стал чёрным

    if( xy.y < (0.25 - 0.2*sin(100.0*xy.x+iTime)*sin(25.0*xy.x)) ||
        xy.y > (0.75 - 0.1*sin(75.0*xy.x+iTime)*sin(50.0*xy.x))
      )

        {LaColora.r = sin(3.14+15.0*xy.y+15.0*xy.x+iTime);
         LaColora.g = sin(10.0*xy.x+iTime);
         LaColora.b = sin(15.0*xy.y+15.0*xy.x+iTime);
        }
    else
        {LaColora.r = sin(15.0*xy.y+15.0*xy.x+iTime);
         LaColora.g = sin(3.14+10.0*xy.x+iTime);
         LaColora.b = sin(3.14+15.0*xy.y+15.0*xy.x+iTime);
        }

fragColor = LaColora;

}
