void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 st = fragCoord/iResolution.xy;
    float aspect = 1280.0 / 720.0; 
    st.x *= aspect;

    vec3 col = vec3(1.0);
    float pct = 0.0;
    float d = distance(st,vec2(0.5*aspect,0.5));

    // pct = step(.3,d);
    // 使用smoothstep()函数，通过修改不同的值来试着做出一个边界顺滑的圆。
    pct = smoothstep(.26,.3,d);

    // 随距离衰减颜色。
    // col = vec3(d*pct*col);

    // 前景色和背景色。
    // col = vec3(1.0)-(d*pct*col);

    // 一闪一闪亮晶晶
    col = d*pct*col + (1.0-pct)*abs(sin(iTime));

    // 心脏跳动？
    col = d*pct*col + (1.0-pct)*pct*abs(sin(iTime));

    // Output to screen
    fragColor = vec4(col,1.0);
}
