precision highp float;

const float PI = 3.14;
const float e = 2.71;

const float R0 = e/PI;

const vec4 color = 1. - vec4(10., 102., 194., 255.)/255.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( 2. * fragCoord - iResolution.xy ) / iResolution.y;
    fragColor =  vec4(exp((-(abs(length(uv) - R0)) + 0.01)/(0.02 + abs(sin(iTime))/20.))) * color;
}
