void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float len = length(iResolution.xy/2.0 - fragCoord);
    float ccos = (cos(len/(iMouse.x * .1) - iTime * (iMouse.x/10.0 + 3.0)) + 1.0) / 2.0 * 1.5;
    float csin = (sin(len/(iMouse.y * .5) - iTime * -(iMouse.y/10.0 + 2.0)) + 1.0) / 2.0;

    // Output to screen
    fragColor = vec4( vec3( 1.0 - csin, (ccos + csin) / 2.0, ccos - csin  ),1.0);
}
