//Taken by http://www.eecs.umich.edu/courses/eecs380/HANDOUTS/PROJ2/InsidePoly.html by Randolph Franklin


const vec2[] Points = vec2[] (
vec2(0.91,4.52),
vec2(2.6,5),
vec2(2.56,-0.61),
vec2(-2.16,-0.07),
vec2(-3.43,4.46),
vec2(-0.78,0.29),
vec2(-0.84,5.65),
vec2(0.1,.87),
vec2(1.71,.87)
);

const int count = 9;

float pnpoly(vec2 p)
{
    int i, j;
    float c = 0.0;
    for (i = 0, j = count - 1; i < count; j = i++)
    {
        if ((((Points[i].y <= p.y) && (p.y < Points[j].y)) || ((Points[j].y <= p.y) && (p.y < Points[i].y))) && (p.x < (Points[j].x - Points[i].x) * (p.y - Points[i].y) / (Points[j].y - Points[i].y) + Points[i].x))
        c = 1.0f - c;

    }
    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv *= 7.;
    uv += vec2(-3.5,-1.);

    // Time varying pixel color
    float col = pnpoly(uv);

    // Output to screen
    fragColor = vec4(vec3(col),1.0);
}
