void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output buffer to screen using color map
    // Color map stored in iChannel1
    // I recomend using CET-R4 from colorcet.com/gallery.html
    fragColor = texture(iChannel1, vec2(texture(iChannel0, uv).r, 1.));
}

const float speed = 4.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    // Check if right edge of screen
    if (fragCoord.x > iResolution.x-speed){
        // Set value to output of audio channel
        fragColor = texture(iChannel0, vec2(fragCoord.y/iResolution.y, 0));
    }else{
        // Scroll across screen
        fragColor = texture(iChannel1, vec2((fragCoord.x+speed)/iResolution.x, fragCoord.y/iResolution.y));
    }
}
