vec3 colorize(int num) {
    return (num<=0?vec3(8  , 24 , 32 ):
           (num==1?vec3(52 , 104, 86 ):
           (num==2?vec3(136, 192, 112):
                   vec3(224, 248, 208))))/255.0;
}

vec2 transform(vec2 p, float d, vec2 dpos, float s, vec2 spos) { //point, translate, angle, rotation point, scale factor
    return s*(vec2((p.x-dpos.x)*cos(d)-(p.y-dpos.y)*sin(d), (p.y-dpos.y)*cos(d)+(p.x-dpos.x)*sin(d))+dpos-spos)+spos;
}

int checkers(vec2 p) {
    int xeven = (floor(floor(p.x)/2.0)==floor(p.x)/2.0)?0:1;
    int yeven = (floor(floor(p.y)/2.0)==floor(p.y)/2.0)?0:1;
    if (xeven==yeven) {
        return 1;
    } else {
        return 0;
    }
}

int plaid(vec2 p) {
    int xeven = (floor(floor(p.x)/2.0)==floor(p.x)/2.0)?0:1;
    int yeven = (floor(floor(p.y)/2.0)==floor(p.y)/2.0)?0:1;
    return xeven+yeven;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 translate = vec2(cos(iTime/2.0+3.14)*4.0);

    float angle = iTime*1.3;
    vec2 rPoint = iResolution.xy/2.0;

    float sFactor = (sin(iTime*2.0)+2.0)/iResolution.y;
    vec2 sPoint = iResolution.xy/2.0;

    vec2 tformedc = transform(floor(fragCoord/iResolution.y*36.0)*iResolution.y/36.0, angle, rPoint, sFactor, sPoint)+translate;
    vec2 tformedp = transform(fragCoord, 0.0, vec2(0), 36.0/iResolution.y, vec2(0));

    //int final = int(floor(mod(tformed.x, 4.0)));
    int final = checkers(tformedc);
    if (final!=0) {
        final += plaid(tformedp);
    }

    fragColor = vec4(colorize(final),1.0);
}
