// noise


#define X 14.743529f   // 0.0 ~ 1000000.0000
#define Y 58.086473f  // 0.0 ~ 1000000.0000
#define size 1923.0f

float noise(vec2 uv){
     float noi = fract(sin(dot(uv.xy, 
                    vec2(X, Y*iTime)))
                    *size);
    return noi;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.x;


    // Output to screen
    fragColor = vec4(vec3(noise(uv)), 1.0);
}
