float N21(vec2 p) {
    return fract(sin(p.x*100.+p.y*6574.)*5647. + iTime * 0.2);
}

float smoothNoise(vec2 uv){
    vec2 lv = fract(uv);
    vec2 id = floor(uv);

    lv = lv*lv*(3.-2.*lv);

    float bl = N21(id);    //bottom left
    float br = N21(id+ vec2(1,0));   //bottom right
    float b = mix(bl, br, lv.x);

    float tl = N21(id + vec2(0,1));    //bottom left
    float tr = N21(id+ vec2(1,1));   //bottom right
    float t = mix(tl, tr, lv.x);

    float c = mix(b, t, lv.y);

    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float c = smoothNoise(uv*2.);
    c += smoothNoise(uv*4.)*.5;
    c += smoothNoise(uv*8.)*.25;
    c += smoothNoise(uv*16.)*.125;
    c += smoothNoise(uv*32.)*.0625;

    c /= 2.;

    float delta = c*clamp(abs(sin(iTime*1.1)),0.3, 1.0);

    vec3 col = vec3(c);


    col = mix(vec3(0.4,1.0,1.2), vec3(1.0,0.0,0.0), c);


    // Output to screen
    fragColor = vec4(col,1.0);
}
