void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

                      //须知：iTime是动态变量，官方提供的功能，类似frameCount，从0开始无限计时
                      //已知：shader参数范围是0~1，可以使用三角函数cos（），sin（）限制iTime范围
                      //已知：sin（），cos（）范围是-1~1
                      //计算：使用abs()绝对值功能，让-1~1范围进一步缩小到0~1期间
    // fragColor = vec4(uv.x ,uv.y ,iTime ,1.0);
    // fragColor = vec4(uv.x ,uv.y ,sin(iTime) ,1.0);
    fragColor = vec4(uv.x ,uv.y ,abs(sin(iTime)) ,1.0);
}

