void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 u = fragCoord/iResolution.xy;
    vec4 a = texture(iChannel0,u);
         //a = abs(a);
         a.z = pow(a.z,.15);
    fragColor = sin(a.z*5.+vec4(1,2,3,4))*.5+.5;
}

#define A(u) texture(iChannel0,(u)/iResolution.xy)
void mainImage( out vec4 fragColor, in vec2 u )
{
    vec4  r = vec4(0);
    vec4  a = A(u);
    float z    = 10.;//kernel convolution size
    for(float i=-z; i<=z; ++i){
    for(float j=-z; j<=z; ++j){
        float l = length(vec2(i,j));
        if((i==0.&&j==0.)|| l>z+.1){continue;}
        vec2  c = normalize(vec2(i,j));
        vec4  a2= A(u+vec2(i,j));
        vec4  b = a2-a; 
        r.xy += c*b.z*cos(l*.66)*exp(-l*l*.06)*.1;
        //r.z  += dot(c,b.xy   2        );//*a2.z;
        //r.w  += dot(c,b.yx*vec2(-1,1));//*a2.z;
    }}
    fragColor = r;
}

#define A(u) texture(iChannel0,(u)/iResolution.xy)
#define B(u) texture(iChannel1,(u)/iResolution.xy)
#define C(u) texture(iChannel2,(u)/iResolution.xy)
void mainImage( out vec4 fragColor, in vec2 u )
{
    vec4 t = A(u);
    vec2 m = +t.xy
             +B(u).xy;
    float s = 0.;
    float z    = 6.;//kernel convolution size
    float blur = 8./z;
    for(float i=-z; i<=z; ++i){
    for(float j=-z; j<=z; ++j){
        //if(length(vec2(i,j))>z+.1){continue;}
        vec2 c = (m+vec2(i,j))*blur;
        s += exp(-dot(c,c));
    }}
    if(s==0.){s = 1.;}
    s = 1./s;

    fragColor = vec4(m,s,0);
}

#define A(u) texture(iChannel0,(u)/iResolution.xy)
#define B(u) texture(iChannel1,(u)/iResolution.xy)
void mainImage( out vec4 fragColor, in vec2 u )
{
    float tz = 0.;
    vec4 a = vec4(0);
    float z    = 6.;//kernel convolution size
    float blur = 8./z;
    for(float i=-z; i<=z; ++i){
    for(float j=-z; j<=z; ++j){
        //if(length(vec2(i,j))>z+.1){continue;}
        vec4 t = A(u+vec2(i,j));
        vec4 m = B(u+vec2(i,j));
        vec2 c = (m.xy-vec2(i,j))*blur;
        float z = t.z*exp(-dot(c,c));
        a.xy += z*m.xy;
        a.z  += z*m.z;
        tz   += z;
    }}
    if(tz==0.){tz = 1.;}
    a.xy /= tz;
    //a = A(u);
    if(iMouse.z>0.)
    {
        vec2 m = 32.*(u-iMouse.xy)/iResolution.y;
        a += vec4(-1,0,0,0)*.1*exp(-dot(m,m));
    }
    if(iFrame==0)
    {
        vec2 m;
        m = 16.*(u-iResolution.xy*.5)/iResolution.y;
        a+= vec4(0,0,1,0)*16.*exp(-dot(m,m));
    }
    fragColor = a;
}
