// 1. / tanh(a*cos(0)) ensures it hits +-1.
float thc(float a, float b) {
    return tanh(a * cos(b)) / tanh(a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;

    float t = thc(3.5, 0.8 * iTime);

    float r = 16. + 8. * t;
    float ix = floor(r * uv.x) + 0.5;
    uv.x = fract(r * uv.x) - 0.5;

    vec2 p = 0.2 * r * vec2(0., thc(2., 2. * iTime + 2. * ix));

    float d = length(vec2(uv.x, r * uv.y) - p);
    float s = step(d, (0.015 - 0.005 * t) * r);

    fragColor = vec4(s);
}
