float thc(float a, float b) {
    return tanh(a * cos(b)) / tanh(a);
}    

float ths(float a, float b) {
    return tanh(a * sin(b)) / tanh(a);
} 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;

    float sc = 96. + 32. * thc(12., iTime);
    vec2 ipos = floor(sc * uv) + 0.5;
    vec2 fpos = fract(sc * uv) - 0.5;

    float a = atan(ipos.y, ipos.x);
    float r = length(ipos)/sc;
    r *= .5 * (1. + thc(4., 4. * r));
    float str = 3. + 5. * cos(3. * a + iTime);
    ipos = r * vec2(thc(str, a), ths(str, a));

    float d = length(ipos);
    float cr = 0.26 + 0.1 * thc(4., iTime) + 0.02 * ths(3., iTime);
    float s = step(cr - 4./sc, d) - step(cr, d);
    s *= step(length(fpos), 0.5);
    fragColor = vec4(s);
}
