// fixing https://shadertoy.com/view/fl3GzS
// variant of https://shadertoy.com/view/Nlc3RS

void mainImage( out vec4 O, vec2 U )
{
        O = T(U/1.);
        if ( iMouse.z > 0.) O =  texelFetch(iChannel1, ivec2(U),0);
}

// --- compute the Collatz sequence

/*
